CREATE TABLE IF NOT EXISTS pokedata_db (
    `ID` INT,
    `Name` VARCHAR(10) CHARACTER SET utf8,
    `HP` INT,
    `Attack` INT,
    `Defense` INT,
    `Type_I` VARCHAR(8) CHARACTER SET utf8,
    `Type_II` VARCHAR(8) CHARACTER SET utf8,
    `Ability_I` VARCHAR(12) CHARACTER SET utf8,
    `Ability_II` VARCHAR(12) CHARACTER SET utf8,
    `Hidden_Ability` VARCHAR(12) CHARACTER SET utf8,
    `Mass` VARCHAR(8) CHARACTER SET utf8,
    `Color` VARCHAR(6) CHARACTER SET utf8,
    `Gender` VARCHAR(9) CHARACTER SET utf8,
    `Evolve` VARCHAR(26) CHARACTER SET utf8,
    `Description` VARCHAR(502) CHARACTER SET utf8,
    `Image` VARCHAR(67) CHARACTER SET utf8
);
